import React, { Component } from "react";
import AuctionContract from "./contracts/Auction.json";
import getWeb3 from "./getWeb3";

import "./App.css";

class App extends Component {
  state = { 
  	bid: 0, 
  	web3: null, 
  	accounts: null, 
  	contract: null,
  	highestbid: 0,
  	highestbidder: "",
  	can_withdraw: null,
  	balance: null,
  	input: "" 
  	};

  componentDidMount = async () => {
    try {
      // Get network provider and web3 instance.
      const web3 = await getWeb3();

      // Use web3 to get the user's accounts.
      const accounts = await web3.eth.getAccounts();

      // Get the contract instance.
      const networkId = await web3.eth.net.getId();
      const deployedNetwork = AuctionContract.networks[networkId];
      const instance = new web3.eth.Contract(
        AuctionContract.abi,
        deployedNetwork && deployedNetwork.address,
      );

      const high_bid = await instance.methods.highestBid().call();
      const high_bidder = await instance.methods.highestBidder().call();

      // Set web3, accounts, and contract to the state, and then proceed with an
      // example of interacting with the contract's methods.
      this.setState({highestbid: high_bid, highestbidder: high_bidder})
      this.setState({ web3, accounts, contract: instance });
    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(
        `Failed to load web3, accounts, or contract. Check console for details.`,
      );
      console.error(error);
    }
  };

  runBid = async () => {
    const { accounts, contract } = this.state;

    // Stores a given value, 5 by default.
    await contract.methods.bid().send({ from: accounts[0], value: this.state.bid });

    // Get the value from the contract to prove it worked.
    const response_bid = await contract.methods.highestBid().call();
    const response_bidder = await contract.methods.highestBidder().call;

    // Update state with the result.
    this.setState({ highestbid: response_bid, highestbidder: response_bidder });
  };

  runWithdraw = async () => {
  	const { accounts, contract } = this.state;

  	await contract.methods.withdraw().send({ from: accounts[0] });

  	const response_bid = await contract.methods.highestBid().call();
    const response_bidder = await contract.methods.highestBidder().call;

    this.setState({ highestbid: response_bid, highestbidder: response_bidder });
  }

  getValue = (event) => {
    this.setState({input: event.target.value}, () => {
      console.log(this.state.input)
    });
  }

  render() {
    if (!this.state.web3) {
      return <div>Loading Web3, accounts, and contract...</div>;
    }
    return (
      <div className="App">
        <h1>Auction</h1>
        <div>The highestBid is: {this.state.highestBid} Wei</div>
        <div>The highestBidder is: {this.state.highestBidder}</div>
        <div>Your bid is: {this.state.accountBid} Wei</div>

        <div>Enter your bid (Ether):</div>
	<input type='text' onChange={this.getValue}/>
	<button onClick={this.runBid}>Bid</button>
	<button onClick={this.runWithdraw}>Withdraw</button>
      </div>
    );
  }
}

export default App;
